    override func viewDidLoad() {
        super.viewDidLoad()
        
        let request = GADRequest()
        adBanner?.adUnitID = "ca-app-pub-2801246019586788/5413951081"
        adBanner?.rootViewController = self
        adBanner?.delegate = self
        request.testDevices = [kGADSimulatorID]
        
        self.adBanner?.load(request)
    
    }
    
    func generateRandomQuote() -> Int {
        let randomQuote = arc4random_uniform(UInt32(quotes.count))
        return Int(randomQuote)
        
        
    }
    func generateRandomImage() -> Int {
        let randomImage = arc4random_uniform(UInt32(images.count))
        return Int(randomImage)
    }
    
    func fadeIn() {
        self.mainQuote.alpha = 1.0
    }
    
    func fadeOut() {
        self.mainQuote.alpha = 0.0
        
    }
    
    func setQuote() {
        self.fadeOut()
        
        UIView.animate(withDuration: 1.0, animations: {
            
            self.fadeIn()
        })
        
    }

    override func didReceiveMemoryWarning() {
        super.didReceiveMemoryWarning()
        // Dispose of any resources that can be recreated.
    }
    
    @IBAction func next(_ sender: UIButton) {
        setQuote()
        mainQuote.text = quotes[generateRandomQuote()]
        imageView.image = images[generateRandomImage()]
        print(quotes.count)
        print(generateRandomQuote())
    
    }
 
    @IBAction func share(_ sender: UIBarButtonItem) {
        
        let activityVC = UIActivityViewController(activityItems: [mainQuote.text!], applicationActivities: nil)
        activityVC.popoverPresentationController?.sourceView = self.view
        self.present(activityVC, animated: true, completion: nil)
    }
    
    @IBAction func saveImage(_ sender: UIButton) {
        
        mainQuote.backgroundColor = UIColor.clear
        mainQuote.textAlignment = .center
        mainQuote.textColor = UIColor.white
        
        UIGraphicsBeginImageContextWithOptions(mainQuote.bounds.size, false, 0)
        imageView.layer.render(in: UIGraphicsGetCurrentContext()!)
        mainQuote.layer.render(in: UIGraphicsGetCurrentContext()!)
        let imageWithLabel = UIGraphicsGetImageFromCurrentImageContext() // here is the final image
        UIGraphicsEndImageContext()
        
        UIImageWriteToSavedPhotosAlbum(imageWithLabel!,self,nil,nil)
        
            let ac = UIAlertController(title: "Saved!", message: "The image has been saved to your camera roll.", preferredStyle: .alert)
            ac.addAction(UIAlertAction(title: "OK", style: .default))
            present(ac, animated: true)
        }
    
}
    
    

    


        
    



